import 'package:flutter/material.dart';
import 'package:practica3_/theme/app_theme.dart';

class ImagesScreen extends StatefulWidget {
  const ImagesScreen({super.key});

  @override
  State<ImagesScreen> createState() => _MyWidgetState();
}

class _MyWidgetState extends State<ImagesScreen> {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Imagenes',
        style: AppTheme.lightTheme.textTheme.headlineLarge
        )
      ),
      body: ListView(
        children: [
          imageCard(),
          ImageWeb(),
        ],
      ),
    );
  }

  Card imageCard(){
    return Card(
      shape: RoundedRectangleBorder(
        borderRadius: BorderRadius.circular(30),
      ),
      margin:const EdgeInsets.all(30),
      elevation: 10,
       child: Column(
         children: [
           ClipRRect(
              borderRadius: BorderRadius.circular(30),
              child:const Image(image: AssetImage('assets/img/3am.png')
              ),
            ),
            Container(
              padding:const EdgeInsets.all(10),
              child: Text('Ellos son Rich y Chriz Bermudez y juntos son 3AM un grupo musical Venezolano', 
              style: AppTheme.lightTheme.textTheme.headlineMedium,),
            )
         ],
       ),
    );
  }

  Widget ImageWeb(){
    return Center(
      child:Image.network('data:image/jpeg;base64,'),
    );
  }

}